import{_ as i,c as a,o as e,ah as r}from"./chunks/framework.HzbgFGQH.js";const p=JSON.parse('{"title":"脆皮豆腐的做法","description":"","frontmatter":{},"headers":[],"relativePath":"guide/vegetable_dish/脆皮豆腐.md","filePath":"guide/vegetable_dish/脆皮豆腐.md","lastUpdated":1747118079000}'),t={name:"guide/vegetable_dish/脆皮豆腐.md"};function d(s,l,h,n,o,_){return e(),a("div",null,l[0]||(l[0]=[r('<h1 id="脆皮豆腐的做法" tabindex="-1">脆皮豆腐的做法 <a class="header-anchor" href="#脆皮豆腐的做法" aria-label="Permalink to “脆皮豆腐的做法”">​</a></h1><p>浓郁的酱汁裹满豆腐，吃一口就停不下来，别提有多好吃。</p><p>预估烹饪难度：★★★</p><h2 id="必备原料和工具" tabindex="-1">必备原料和工具 <a class="header-anchor" href="#必备原料和工具" aria-label="Permalink to “必备原料和工具”">​</a></h2><ul><li>老豆腐</li><li>鸡蛋</li><li>生抽</li><li>老抽</li><li>蚝油</li><li>白糖</li><li>玉米淀粉</li><li>平底锅</li></ul><h2 id="计算" tabindex="-1">计算 <a class="header-anchor" href="#计算" aria-label="Permalink to “计算”">​</a></h2><p>每份：</p><ul><li>老豆腐 1 块 （市场买 1.25 格 * 人）</li><li>鸡蛋 2 只 * 老豆腐块数</li><li>玉米淀粉 50 g * 老豆腐块数</li><li>生抽 20 g</li><li>蚝油 10 g</li><li>老抽 5 g</li><li>白糖 10 g</li></ul><h2 id="操作" tabindex="-1">操作 <a class="header-anchor" href="#操作" aria-label="Permalink to “操作”">​</a></h2><ul><li>鸡蛋搅拌形成蛋液放置备用</li><li>配置酱料 （20 g 生抽+10 g 蚝油+5 g 老抽+10 g 白糖+10 g 玉米淀粉+200 ml 清水）</li><li>老豆腐切片 （个人建议，仅供参考 人 * 5 片，厚度 1.2 cm）</li><li>玉米淀粉倒入盘中，将老豆腐片粘上淀粉后，粘上蛋液，放置一旁</li><li>热锅，锅内放入 18ml 食用油。等待 10 秒让油温升高</li><li>将粘上蛋液的老豆腐片均匀放入锅中，铺好锅底，小火煎至金黄翻面</li><li>待两面煎至金黄后，倒入酱料，让每块豆腐都沐浴在酱料中，大火 3 分钟至酱汁浓稠</li><li>关火</li></ul><h2 id="附加内容" tabindex="-1">附加内容 <a class="header-anchor" href="#附加内容" aria-label="Permalink to “附加内容”">​</a></h2><p>如果您遵循本指南的制作流程而发现有问题或可以改进的流程，请提出 Issue 或 Pull request 。</p>',12)]))}const g=i(t,[["render",d]]);export{p as __pageData,g as default};
