import{_ as i,c as a,o as e,ag as t}from"./chunks/framework.DPDPlp3K.js";const r="/assets/%E8%8A%B9%E8%8F%9C%E6%8B%8C%E8%8C%B6%E6%A0%91%E8%8F%87.CGCzz86e.jpg",s="/assets/%E9%97%BD%E6%98%9F%E8%8C%B6%E6%A0%91%E8%8F%87.CK9WQ0z7.jpg",m=JSON.parse('{"title":"芹菜拌茶树菇的做法","description":"","frontmatter":{},"headers":[],"relativePath":"guide/vegetable_dish/芹菜拌茶树菇/芹菜拌茶树菇.md","filePath":"guide/vegetable_dish/芹菜拌茶树菇/芹菜拌茶树菇.md","lastUpdated":1747107234000}'),o={name:"guide/vegetable_dish/芹菜拌茶树菇/芹菜拌茶树菇.md"};function d(_,l,n,h,c,u){return e(),a("div",null,l[0]||(l[0]=[t('<h1 id="芹菜拌茶树菇的做法" tabindex="-1">芹菜拌茶树菇的做法 <a class="header-anchor" href="#芹菜拌茶树菇的做法" aria-label="Permalink to &quot;芹菜拌茶树菇的做法&quot;">​</a></h1><p><img src="'+r+'" alt="芹菜拌茶树菇成品"><img src="'+s+'" alt="闽星茶树菇"></p><p>芹菜拌茶树菇是一道简单易做的凉拌菜。富含多种人体所需的维生素和矿物质。一般初学者只需要 30 分钟即可完成。</p><p>预估烹饪难度：★★</p><h2 id="必备原料和工具" tabindex="-1">必备原料和工具 <a class="header-anchor" href="#必备原料和工具" aria-label="Permalink to &quot;必备原料和工具&quot;">​</a></h2><ul><li>闽星茶树菇</li><li>芹菜</li><li>香油</li><li>蚝油</li><li>味极鲜</li><li>食用盐</li></ul><h2 id="计算" tabindex="-1">计算 <a class="header-anchor" href="#计算" aria-label="Permalink to &quot;计算&quot;">​</a></h2><p>每次制作前需要确定计划做几份。一份可够 1-2 人。</p><p>每份：</p><ul><li>闽星茶树菇 1 瓶</li><li>芹菜 250-300g（推荐 30cm 高的芹菜苗,没有芹菜苗可以用山芹或芹菜代替但是需要稍微焯水断生）</li><li>香油 5ml</li><li>蚝油 大约 7ml</li><li>味极鲜 3ml</li><li>食用盐 大约 2g</li></ul><h2 id="操作" tabindex="-1">操作 <a class="header-anchor" href="#操作" aria-label="Permalink to &quot;操作&quot;">​</a></h2><ul><li>（如果是芹菜苗这一步略过）用热水壶烧一壶热水,备用</li><li>新鲜的芹菜苗或者芹菜摘去黄叶清洗,备用</li><li>（如果是芹菜苗这一步略过）将芹菜摘去叶子单独放一个盆中,将芹菜茎用刀划成 2-3 毫米宽的芹菜条备用,这一步的目的是让芹菜断生的更快更均匀,吃起来更脆更爽口</li><li>芹菜苗切成 4cm 的芹菜段,备用</li><li>（如果是芹菜苗这一步略过）起锅开火,将热水壶的开水倒入锅中待水起泡沸腾</li><li>（如果是芹菜苗这一步略过）将切好的芹菜条放入锅中焯水,大约 20 秒放入芹菜叶,5 秒后关火全部捞出过凉水,备用</li><li>将盆中焯好的芹菜或者芹菜苗撒上准备好的食盐,香油,耗油和味极鲜搅拌均匀</li><li>将茶树菇倒入盆中搅拌均匀</li><li>装盘</li><li>开吃</li></ul><h2 id="附加内容" tabindex="-1">附加内容 <a class="header-anchor" href="#附加内容" aria-label="Permalink to &quot;附加内容&quot;">​</a></h2><ul><li>焯水时的水位需要没过芹菜</li><li>芹菜叶太老太硬的话可以不要</li></ul><p>如果您遵循本指南的制作流程而发现有问题或可以改进的流程，请提出 Issue 或 Pull request 。</p>',15)]))}const g=i(o,[["render",d]]);export{m as __pageData,g as default};
