import{_ as a,c as i,o as e,ag as t}from"./chunks/framework.DPDPlp3K.js";const r="/assets/1.DYQqnJLB.jpeg",o="/assets/2.Dx71zhPe.jpeg",p=JSON.parse('{"title":"茭白炒肉的做法","description":"","frontmatter":{},"headers":[],"relativePath":"guide/vegetable_dish/茭白炒肉/茭白炒肉.md","filePath":"guide/vegetable_dish/茭白炒肉/茭白炒肉.md","lastUpdated":1747107234000}'),s={name:"guide/vegetable_dish/茭白炒肉/茭白炒肉.md"};function h(d,l,n,u,c,_){return e(),i("div",null,l[0]||(l[0]=[t('<h1 id="茭白炒肉的做法" tabindex="-1">茭白炒肉的做法 <a class="header-anchor" href="#茭白炒肉的做法" aria-label="Permalink to &quot;茭白炒肉的做法&quot;">​</a></h1><p>茭白味道鲜美，有一定营养价值</p><p>预估烹饪难度：★★★</p><h2 id="必备原料和工具" tabindex="-1">必备原料和工具 <a class="header-anchor" href="#必备原料和工具" aria-label="Permalink to &quot;必备原料和工具&quot;">​</a></h2><ul><li>茭白</li><li>瘦肉</li><li>淀粉</li><li>食用油</li><li>鸡精</li><li>姜</li><li>蒜</li><li>料酒</li><li>盐</li></ul><h2 id="计算" tabindex="-1">计算 <a class="header-anchor" href="#计算" aria-label="Permalink to &quot;计算&quot;">​</a></h2><p>每份：</p><ul><li>茭白 2 根</li><li>瘦肉 100 g</li><li>淀粉 15 g</li><li>食用油 30 ml</li><li>鸡精 5 g</li><li>姜 1 片</li><li>蒜 1 个</li><li>料酒 5 ml</li><li>盐 2 g</li></ul><h2 id="操作" tabindex="-1">操作 <a class="header-anchor" href="#操作" aria-label="Permalink to &quot;操作&quot;">​</a></h2><h3 id="准备" tabindex="-1">准备 <a class="header-anchor" href="#准备" aria-label="Permalink to &quot;准备&quot;">​</a></h3><ul><li>茭白切片，每片厚度 0.5 cm</li><li>瘦肉切条，厚度 0.3-0.5 cm，加入料酒、生粉、盐、水搅拌</li><li>姜切片、蒜头剁碎</li></ul><h3 id="制作" tabindex="-1">制作 <a class="header-anchor" href="#制作" aria-label="Permalink to &quot;制作&quot;">​</a></h3><ul><li>起锅水烧开，放入茭白，水煮 60-90 S 后取出沥干</li><li>起锅，倒入 15 ml 油，倒入瘦肉，反复翻炒 60 S 取出</li><li>起锅，倒入 15 ml 油，倒入姜、蒜翻炒 30S，加入茭白继续翻炒 30 S</li><li>继续加入瘦肉翻炒 60 S，加入 20 ml 水，加入盐、鸡精后翻炒 60S 出锅</li></ul><p><img src="'+r+'" alt="示例菜成品"><img src="'+o+'" alt="示例菜成品"></p><h2 id="附加内容" tabindex="-1">附加内容 <a class="header-anchor" href="#附加内容" aria-label="Permalink to &quot;附加内容&quot;">​</a></h2><ul><li>最后一个步骤，可以考虑加蚝油、生抽代替盐上色</li></ul><p>如果您遵循本指南的制作流程而发现有问题或可以改进的流程，请提出 Issue 或 Pull request 。</p>',17)]))}const b=a(s,[["render",h]]);export{p as __pageData,b as default};
