import{_ as i,c as a,o as e,ag as t}from"./chunks/framework.DPDPlp3K.js";const r="/assets/1.DJ5v2nAq.jpeg",s="/assets/2.DD0F4TFV.jpeg",m=JSON.parse('{"title":"蚝油三鲜菇的做法","description":"","frontmatter":{},"headers":[],"relativePath":"guide/vegetable_dish/蚝油三鲜菇/蚝油三鲜菇.md","filePath":"guide/vegetable_dish/蚝油三鲜菇/蚝油三鲜菇.md","lastUpdated":1747107234000}'),o={name:"guide/vegetable_dish/蚝油三鲜菇/蚝油三鲜菇.md"};function d(n,l,_,h,c,u){return e(),a("div",null,l[0]||(l[0]=[t('<h1 id="蚝油三鲜菇的做法" tabindex="-1">蚝油三鲜菇的做法 <a class="header-anchor" href="#蚝油三鲜菇的做法" aria-label="Permalink to &quot;蚝油三鲜菇的做法&quot;">​</a></h1><p>几分钟就能做出的蚝油蘑菇，滑嫩入味鲜美可口，别提多好吃了。</p><p>预估烹饪难度：★★★</p><h2 id="必备原料和工具" tabindex="-1">必备原料和工具 <a class="header-anchor" href="#必备原料和工具" aria-label="Permalink to &quot;必备原料和工具&quot;">​</a></h2><ul><li>香菇</li><li>蟹味菇</li><li>白玉菇</li><li>小米辣</li><li>菜椒</li><li>食用油</li><li>盐</li><li>料酒</li><li>淀粉</li><li>生抽</li><li>鸡精</li><li>香葱</li><li>姜末</li><li>蚝油</li><li>西蓝花</li></ul><h2 id="计算" tabindex="-1">计算 <a class="header-anchor" href="#计算" aria-label="Permalink to &quot;计算&quot;">​</a></h2><p>每份：</p><ul><li>鲜香菇 2 朵</li><li>蟹味菇 30 g</li><li>白玉菇 30 g</li><li>小米辣 1 根</li><li>菜椒 0.5 颗</li><li>食用油 10 ml</li><li>食用盐 5 g</li><li>料酒 2 ml</li><li>淀粉 10 g</li><li>生抽 10 ml</li><li>鸡精 3 g</li><li>香葱 0.5 根</li><li>姜末 1 粒</li><li>蚝油 5 ml</li><li>开水 350 ml</li><li>西蓝花 100 g</li></ul><h2 id="操作" tabindex="-1">操作 <a class="header-anchor" href="#操作" aria-label="Permalink to &quot;操作&quot;">​</a></h2><ul><li>蟹味菇、白玉菇 去掉根部泥土，掰散菌朵</li><li>香菇切片（每片厚度 0.5-1 cm，厚点相对薄点更有嚼劲）</li><li>生粉倒入小碗中，加入 50ml 水，搅拌生粉直至融化没有颗粒（即水淀粉）备用</li><li>水开，放入西蓝花，清水煮 3 分钟，放入碗中备用</li><li>洗锅烧开水，加入 5 g 食用盐，倒入蟹味菇、白玉菇、香菇，水煮 1 分钟</li><li>1 分钟后，捞出沥干水分</li><li>起锅烧油，待油开始冒小泡，放入姜末、小米辣、菜椒 煸炒 30 S</li><li>倒入三鲜菇，然后依次倒入生抽、蚝油、鸡精，翻炒均匀后，倒入水淀粉</li><li>中火烧干汁，加入料酒、葱花 出锅</li><li>摆上西蓝花</li></ul><p><img src="'+r+'" alt="示例菜成品"><img src="'+s+'" alt="示例菜成品"></p><h2 id="附加内容" tabindex="-1">附加内容 <a class="header-anchor" href="#附加内容" aria-label="Permalink to &quot;附加内容&quot;">​</a></h2><p>如果您遵循本指南的制作流程而发现有问题或可以改进的流程，请提出 Issue 或 Pull request 。</p>',13)]))}const g=i(o,[["render",d]]);export{m as __pageData,g as default};
