import{_ as i,c as a,o as e,ag as t}from"./chunks/framework.DPDPlp3K.js";const c=JSON.parse('{"title":"金针菇日本豆腐煲的做法","description":"","frontmatter":{},"headers":[],"relativePath":"guide/vegetable_dish/金针菇日本豆腐煲.md","filePath":"guide/vegetable_dish/金针菇日本豆腐煲.md","lastUpdated":1747034379000}'),r={name:"guide/vegetable_dish/金针菇日本豆腐煲.md"};function o(d,l,s,_,n,h){return e(),a("div",null,l[0]||(l[0]=[t('<h1 id="金针菇日本豆腐煲的做法" tabindex="-1">金针菇日本豆腐煲的做法 <a class="header-anchor" href="#金针菇日本豆腐煲的做法" aria-label="Permalink to &quot;金针菇日本豆腐煲的做法&quot;">​</a></h1><p>金针菇日本豆腐煲是一道容易上手的日常料理。</p><p>预估烹饪难度：★★</p><h2 id="必备原料和工具" tabindex="-1">必备原料和工具 <a class="header-anchor" href="#必备原料和工具" aria-label="Permalink to &quot;必备原料和工具&quot;">​</a></h2><ul><li>金针菇</li><li>日本豆腐（玉子豆腐）</li><li>小米椒</li><li>生抽</li><li>蚝油</li><li>盐</li><li>糖</li><li>食用油</li></ul><h2 id="计算" tabindex="-1">计算 <a class="header-anchor" href="#计算" aria-label="Permalink to &quot;计算&quot;">​</a></h2><p>每份：</p><ul><li>金针菇 1-2 把</li><li>豆腐 2 袋</li><li>小米椒 3-5 根，切碎</li><li>蒜 2-3 瓣</li><li>生抽 15ml</li><li>蚝油 5ml</li><li>老抽 3ml</li><li>糖 3g</li><li>食用油 10-15ml</li></ul><h2 id="操作" tabindex="-1">操作 <a class="header-anchor" href="#操作" aria-label="Permalink to &quot;操作&quot;">​</a></h2><ul><li>豆腐切片，小火煎到两面金黄出锅备用。</li><li>切蒜成蒜末；将生抽，蚝油，老抽，糖，100ml 水调汁备用。</li><li>热锅放油，油热放小米椒、蒜末爆香，先放金针菇，炒软，把煎好的豆腐平铺在金针菇上，倒入#2 配好的料汁，焖 5 分钟，大火收汁。</li></ul><h2 id="附加内容" tabindex="-1">附加内容 <a class="header-anchor" href="#附加内容" aria-label="Permalink to &quot;附加内容&quot;">​</a></h2><ul><li>金针菇一定要先炒软</li><li>豆腐尽量不要翻炒，容易碎</li></ul><p>如果您遵循本指南的制作流程而发现有问题或可以改进的流程，请提出 Issue 或 Pull request 。</p>',13)]))}const p=i(r,[["render",o]]);export{c as __pageData,p as default};
