import{_ as i,c as e,o as a,ag as t}from"./chunks/framework.DPDPlp3K.js";const u=JSON.parse('{"title":"陕北熬豆角的做法","description":"","frontmatter":{},"headers":[],"relativePath":"guide/vegetable_dish/陕北熬豆角.md","filePath":"guide/vegetable_dish/陕北熬豆角.md","lastUpdated":1747107234000}'),r={name:"guide/vegetable_dish/陕北熬豆角.md"};function o(d,l,s,m,n,h){return a(),e("div",null,l[0]||(l[0]=[t('<h1 id="陕北熬豆角的做法" tabindex="-1">陕北熬豆角的做法 <a class="header-anchor" href="#陕北熬豆角的做法" aria-label="Permalink to &quot;陕北熬豆角的做法&quot;">​</a></h1><p>陕北熬豆角是一种对初学者极其友善的菜，因其制作方式使用<code>熬</code>的方式，食材可多可少，可有可无，几乎不存在失败的可能性。</p><p>预估烹饪难度：★★</p><h2 id="必备原料和工具" tabindex="-1">必备原料和工具 <a class="header-anchor" href="#必备原料和工具" aria-label="Permalink to &quot;必备原料和工具&quot;">​</a></h2><ul><li>豆角</li><li>土豆</li><li>西红柿</li><li>螺丝椒（可选）</li><li>盐</li><li>生抽</li><li>五香粉</li><li>蚝油</li><li>葱</li><li>姜</li><li>蒜</li></ul><h2 id="计算" tabindex="-1">计算 <a class="header-anchor" href="#计算" aria-label="Permalink to &quot;计算&quot;">​</a></h2><p>注意，受食材个体影响，本菜谱为 2 人份菜谱</p><ul><li>豆角 300g * 2 人</li><li>土豆 1 个 * 2 人</li><li>西红柿 1 个 * 2 人</li><li>螺丝椒（可选）2 个 * 2 人</li><li>盐 6g * 2 人</li><li>生抽 6ml * 2 人</li><li>五香粉 3g * 2 人</li><li>蚝油 6ml * 2 人</li><li>葱 3 圈</li><li>姜 2g</li><li>蒜 2 颗</li><li>香菜碎（可选）根据口味加</li></ul><h2 id="操作" tabindex="-1">操作 <a class="header-anchor" href="#操作" aria-label="Permalink to &quot;操作&quot;">​</a></h2><ul><li>葱切花，蒜切沫，姜切丝，备用。</li><li>豆角去筋，切<em>2-10cm</em>小段，备用。</li><li>土豆去皮，切<em>1cm³</em>小块，备用。</li><li>西红柿去皮，切<em>1cm³</em>小块，备用。</li><li>辣椒去仔，切<em>0.15cm 宽</em>条，备用。</li><li>起锅烧油(10ml - 15ml)，冒烟后放入葱姜蒜，翻炒至闻到葱姜蒜香味；</li><li>加入豆角，翻炒至变色（青绿色变为翠绿色）；</li><li>加入土豆块，翻炒 30s；</li><li>加入热水（水面刚刚漫过菜），盖上锅盖熬至土豆<em>变软</em>（可以用筷子确认）；</li><li>加入西红柿块，加入盐，生抽，蚝油，五香粉，辣椒，熬至西红柿成汁（注意搅拌，防止糊锅）；</li><li>加入香菜碎，出锅。</li></ul><h2 id="附加内容" tabindex="-1">附加内容 <a class="header-anchor" href="#附加内容" aria-label="Permalink to &quot;附加内容&quot;">​</a></h2><ul><li>加入西红柿后开始继续熬制后，需要偶尔翻搅防止糊底</li></ul><p>如果您遵循本指南的制作流程而发现有问题或可以改进的流程，请提出 Issue 或 Pull request 。</p>',13)]))}const _=i(r,[["render",o]]);export{u as __pageData,_ as default};
