import{_ as i,c as e,o as a,ah as r}from"./chunks/framework.HzbgFGQH.js";const p=JSON.parse('{"title":"鸡蛋火腿炒黄瓜的做法","description":"","frontmatter":{},"headers":[],"relativePath":"guide/vegetable_dish/鸡蛋火腿炒黄瓜.md","filePath":"guide/vegetable_dish/鸡蛋火腿炒黄瓜.md","lastUpdated":1747108385000}'),t={name:"guide/vegetable_dish/鸡蛋火腿炒黄瓜.md"};function d(o,l,s,n,c,h){return a(),e("div",null,l[0]||(l[0]=[r('<h1 id="鸡蛋火腿炒黄瓜的做法" tabindex="-1">鸡蛋火腿炒黄瓜的做法 <a class="header-anchor" href="#鸡蛋火腿炒黄瓜的做法" aria-label="Permalink to “鸡蛋火腿炒黄瓜的做法”">​</a></h1><p>预估烹饪难度：★★</p><h2 id="必备原料和工具" tabindex="-1">必备原料和工具 <a class="header-anchor" href="#必备原料和工具" aria-label="Permalink to “必备原料和工具”">​</a></h2><ul><li>黄瓜</li><li>鸡蛋</li><li>火腿肠</li><li>盐</li><li>生抽</li><li>红尖椒（可选）</li></ul><h2 id="计算" tabindex="-1">计算 <a class="header-anchor" href="#计算" aria-label="Permalink to “计算”">​</a></h2><p>每次制作前需要确定计划做几份。一份正好够 1 个人吃。</p><p>每份：</p><ul><li>黄瓜 1 根（约 200g）</li><li>鸡蛋 2 个</li><li>火腿肠 1 根（约 40g）</li><li>红尖椒 1 个（可选）</li><li>食用油 10ml</li><li>生抽 3ml</li><li>盐 2g</li></ul><h2 id="操作" tabindex="-1">操作 <a class="header-anchor" href="#操作" aria-label="Permalink to “操作”">​</a></h2><ul><li>黄瓜洗净，切半圆形片，备用</li><li>火腿切半圆形片，备用</li><li>红尖椒（可选）切碎，备用</li><li>将鸡蛋打入碗中，搅匀，即为 <code>鸡蛋液</code></li><li>热锅里倒 5ml 食用油</li><li>油热后转小火，倒入打散的<code>鸡蛋液</code>，用筷子划散，翻炒至鸡蛋结为固体且颜色微微发黄，即为 <code>半熟鸡蛋</code>，盛出备用</li><li><strong>不用洗锅</strong>，往锅内倒入 5ml 食用油，倒入黄瓜片大火<strong>翻炒 1 分钟</strong></li><li>把 <code>半熟鸡蛋</code> 倒入锅中，调入 2g 盐、3ml 生抽，立刻倒入火腿片和辣椒碎（可选）翻炒均匀</li><li>关火，盛盘</li></ul><h2 id="附加内容" tabindex="-1">附加内容 <a class="header-anchor" href="#附加内容" aria-label="Permalink to “附加内容”">​</a></h2><ul><li>火腿是咸的，要在调味后才加入，这样不会让火腿也沾上盐</li></ul><p>如果您遵循本指南的制作流程而发现有问题或可以改进的流程，请提出 Issue 或 Pull request 。</p>',13)]))}const m=i(t,[["render",d]]);export{p as __pageData,m as default};
